import os
import uuid
from generator.template.utility import writer

template = """
//*************************************************************************************
//   !!! Generated by the fmp-cli {{version}}.  DO NOT EDIT!
//*************************************************************************************

using System.Collections.Generic;

namespace {{org_name}}.FMP.MOD.{{module_name}}.LIB.Unity
{
    /// <summary>
    /// 序列类基类
    /// </summary>
    public class SequenceBase
    {
        public System.Action OnFinish;
    } //class

    /// <summary>
    /// 计数器序列
    /// </summary>
    public class CounterSequence : SequenceBase
    {
        protected int total_ { get; set; }
        protected int ticker_ { get; set; }

        /// <summary>
        /// 构造函数
        /// </summary>
        /// <param name="_total">初始化总数</param>
        public CounterSequence(int _total)
        {
            total_ = _total;
            ticker_ = 0;
        }

        /// <summary>
        /// 总数加一
        /// </summary>
        public void Dial()
        {
            total_ += 1;
        }

        /// <summary>
        /// 计数器加一
        /// </summary>
        public void Tick()
        {
            if (ticker_ >= total_)
                return;

            ticker_ += 1;
            if (ticker_ >= total_)
            {
                if (null != OnFinish)
                {
                    OnFinish();
                }
            }
        }

    } //class
} //namespace

"""

def generate(_options, _outputdir: str):
    output_dir = os.path.join(_outputdir, "Assets")
    os.makedirs(output_dir, exist_ok=True)
    output_dir = os.path.join(output_dir, "Scripts")
    os.makedirs(output_dir, exist_ok=True)
    output_dir = os.path.join(output_dir, "Module")
    os.makedirs(output_dir, exist_ok=True)
    output_dir = os.path.join(output_dir, "_Generated_")
    os.makedirs(output_dir, exist_ok=True)

    contents = template
    contents = contents.replace("{{org_name}}", _options["org_name"])
    contents = contents.replace("{{module_name}}", _options["module_name"])
    contents = contents.replace("{{version}}", _options["version"])
    output_path = os.path.join(output_dir, "Sequence.cs")
    writer.write(output_path, contents, True)
